for i in en-*; do grep "TranslationModel0" $i/direct/mert-work/moses.ini > temp; cat $i/comboall/mert-work/moses.ini > $i/moses-combo-augments-direct.ini; sed -i 's/Model0/Model9/g' temp; sed -i "s/8 T 8/&\n9 T 9/g" $i/moses-combo-augments-direct.ini;sed -i "s@^PhraseDictionaryBinary.*Model8.*@&\n$(head -1 temp)@g" $i/moses-combo-augments-direct.ini; sed -i "s/^TranslationModel8.*/&\n$(tail -1 temp)/g" $i/moses-combo-augments-direct.ini; vi $i/moses-combo-augments-direct.ini; done
